import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

function WeatherIcon(weatherDisp){
  console.log('weatherDisp Input',weatherDisp);
    var weatherDispType;
    if (weatherDisp == "Clear"){
      weatherDispType = "sun";
    }
    if (weatherDisp == "Rain"){
      weatherDispType = "cloud-rain";
    }
    if (weatherDisp == "Clouds"){
      weatherDispType = "cloud";
    }

  console.log('weatherDisp Output',weatherDisp);
  console.log('the icon to be shown', weatherDispType);
  return (
      <FontAwesomeIcon icon={weatherDispType} size="6x" />
  );
}
export default WeatherIcon;

