import React from 'react';

export default function PageWrapper({clouds,children}){
    const wrapperOpacity = clouds ? (clouds * 0.01) : 0;
    const currentSky = "189,195,199";
    const updatedCurrentSky = "246,71,71";

    if (cloudy > 75){
      return(
        <div style={{height:"100%",width:"100%",minHeight:"100vh",minWidth:"100vw",backgroundColor:`rgba(${currentSky}, ${wrapperOpacity})`}} className="PageWrapper cloudy">
            {children}
        </div>
      )
    }
    else{
      return(
        <div style={{height:"100%",width:"100%",minHeight:"100vh",minWidth:"100vw",backgroundColor:`rgba(${updatedCurrentSky}, ${wrapperOpacity})`}}>
          <div className="PageWrapper sun">
            {children}
          </div>
        </div>
      )
    }
}
