import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

import { faCloud, faSun, faCloudRain} from '@fortawesome/free-solid-svg-icons'

export default function WeatherIcon(weatherDisp){
  console.log(weatherDisp)
  switch (weatherDisp){
    case "Clear":
      return (
        <FontAwesomeIcon
          icon={faSun}
          className = "WeatherIcon"
          size="6x"
        />
      )

    case "Rain":
      return (
        <FontAwesomeIcon
          icon={faCloudRain}
          className = "WeatherIcon"
          size="6x"
        />
      )
    case "Clouds":
      return (
          <FontAwesomeIcon
          icon={faCloud}
          className = "WeatherIcon"
          size="6x"
          />
      )
    default:
      return (
          <FontAwesomeIcon
          icon={faSun}
          className = "WeatherIcon"
          size="6x"
          />
      )
  }
}


