}, [weather])
*/

  console.log('weather', weather);

  return(
    /*
    <div>
      <h1>Weather in {city}</h1>
      <p>Humidity: {weather.data && weather.data.main.humidity}</p>
    </div>
    */
      <div>
      <div className="WeatherNav">
        <a className={`WeatherNav__Item`} href="/?city=Seoul">Seoul</a>
        <a
          className={`WeatherNav__Item ${city =='Seoul' ? 'WeatherNav__Item--active' : ''}`}
          href="/?city=Seoul"
        >
          Seoul
        </a>
        <a className={`WeatherNav__Item ${city =='London' ? 'WeatherNav__Item--active' : ''}`} href="/?city=Seoul">London</a>
        <a className="WeatherNav__Item" href="/?city=Miami">Miami</a>
        <a className="WeatherNav__Item" href="/?city=Chicago">Chicago</a>
        <h1>Weather in: <span>{city}</span></h1>
      </div>
      </div>
  );
}

